mapper.update:
UPDATE product

     product_name= #{product.product_name,jdbcType=VARCHAR},
     product_info= #{product.product_info,jdbcType=VARCHAR},
     company_id= #{product.company_id,jdbcType=INTEGER},
     coment= #{product.coment,jdbcType=VARCHAR}

WHERE product_id = #{product.product_id,jdbcType=INTEGER}
